// NODE$LISTEN //
/////////////////
module.exports = function( write, server ) {
  var http = require( "http" )
    ;
  ////////////////////////////////
  // IF NO SERVER EXIST FOR NOW //
  ////////////////////////////////
  if ( ! ( server instanceof http.Server ) ) {
    var where = server;//save port in where
    server = http.createServer();//create webserver 
    server.listen( where );//Add port to server
  }//END IF
  
  return fab.queue( 
    function ( queue ) {
      queue( 
        fab.listener(
          //ARGUMENT IS BODY FOR LISTENER EVENTS 
          function( listener ) { server.on( "request", listener ); }
        )//END FAB.LISTENER
      )//END QUEUE
       
      return queue( write );
  });//END FAB.QUEUE
}//END LISTEN