// RENDERER //
///////////////////////////////
// tree: tree from a bif obj //
// pages: pagetree obj       //
// page: name/id of the page //
///////////////////////////////
module.exports = function ( tree, pages, page ) {
  var css = require( './css' )( tree )
    , tpl = require( './tpl' )( tree, pages, page, false ) 
    , script = require( './script' )( tree )
    ;
  
  return {
    css : css
  , tpl: tpl.tpl
  , menu: tpl.menu  
  , script: script
  };
};//END RENDERER