module.exports = function ( tree ) { 
  var script = ''
  
  
  for ( var i in tree ) if ( tree[ i ].script && tree[ i ].script.length ) { 
    script += tree[ i ].script + '\n';
    script += "$( '#" + tree[ i ].ident + "')." + tree[ i ].script.replace( / /ig, '' ).split('.', 3)[ 2 ].split( '=' )[ 0 ] + "();\n"
    console.log(  tree[ i ].script.replace( / /ig, '' ).split('.', 3)[ 2 ].split( '=' )[ 0 ] )
  
  }
  return script;
}//END SCRIPT
;